import React, { useState } from "react";
import { useTasksContext } from "../hooks/useTasksContext";
import "./css/TaskForm.css";

const TaskForm = () => {
  const { tasks, dispatch } = useTasksContext();
  const [task, setTask] = useState({ description: "", isEdit: false, isDone: false });
  const [error, setError] = useState("");
  const [emptyFields, setEmptyFields] = useState([]);

  function handleChange(inputIdentifier, value) {
    setTask({ ...task, [inputIdentifier]: value });
  }

  const handleSubmit = async e => {
    e.preventDefault();
    // if (!task.description) alert("Enter a task");
    // else {
    //   dispatch({ type: "CREATE_TASK", payload: { ...task, _id: tasks.length + 1 } });
    //   setTask({ _id: 0, description: "", isEdit: false, isDone: false });
    //   console.log(tasks);
    // }
    // const response = await fetch("http://localhost:4000/api/tasks", {
    //   method: "POST",
    //   body: JSON.stringify(task),
    //   headers: {
    //     Accept: "application/json",
    //     "Content-Type": "application/json"
    //   }
    // });
    // const json = await response.json();

    // if (!response.ok) {
    //   setError(json.error);
    //   setEmptyFields(json.emptyFields);
    // }
    // if (response.ok) {
    //   console.log("New task added successfully: ", json);
    //   setTask({ description: "", isEdit: false, isDone: false });
    //   dispatch({ type: "CREATE_TASK", payload: json });
    // }

    const options = {
      method: "POST",
      body: JSON.stringify(task),
      headers: {
        "Content-Type": "application/json"
      }
    };

    const json = await fetch("http://localhost:4000/api/tasks", options)
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Something went wrong ...");
        }
      })
      .catch(error => this.setState({ error }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={task.description} onChange={e => handleChange("description", e.target.value)} className={emptyFields.includes("description") ? "error" : ""} />
      <button>Add Task</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default TaskForm;
